import React, {Component} from 'react';
import { useEffect, useState } from 'react';


class Nav extends Component {
    constructor(props) {
        super(props);
        this.state = {
           pageIndex: this.props.page,
           productNav: "nonactive",
           paymentNav: "nonactive",
        }

        this.setActiveNav=this.setActiveNav.bind(this.state.pageIndex);
    }

    setActiveNav(pageIndex){
        if (pageIndex=="products") this.state.productNav="active";
        else this.state.paymentNav = "active";
        return 1;
    };
  
    render() {
        return (
       
            <nav className="navbar navbar-inverse bg-primary">
            <div className="container-fluid">
                <div className="navbar-header">
    
                <a className="navbar-brand" href="#"> 
                WebDemo
                </a>
                
                </div>
                <ul className="nav navbar-nav">
                <li className={this.state.productNav}><a href="/products">Product</a></li>
                <li className={this.state.paymentNav}><a href="/payment">Payment</a></li>
                </ul>
                <ul className="nav navbar-nav navbar-right">
                <li><a href="#"><span className="glyphicon glyphicon-user"></span> Sign Up</a></li>
                <li><a href="#"><span className="glyphicon glyphicon-log-in"></span> Login</a></li>
                </ul>
            </div>
            </nav>

                       
            
        
        );
    }
}

export default Nav;
