import React, {Component} from 'react';
import { useEffect, useState } from 'react';


class Nav extends Component {
    // constructor(props) {
    //     super(props);
    //     this.state = {
    //        pageIndex: this.props.page,
    //        productNav: "nonactive",
    //        paymentNav: "nonactive",
    //     }

    //     this.setActiveNav.bind(this.state.pageIndex);
    // }
    state = {
               productNav: "nonactive",
               paymentNav: "nonactive",
               payment: this.setActiveNav.bind(this.props.page)
            };

    setActiveNav(pageIndex){
        if (pageIndex=="products") this.state.productNav="active";
        else this.state.paymentNav = "active";
        console.log(this.props.page);
        return 1;
        
    };
  
    render() {
        return (
       
            <nav className="navbar navbar-inverse bg-primary">
            <div className="container-fluid">
                <div className="navbar-header">
    
                <a className="navbar-brand" href="#"> 
                WebDemo
                </a>
                
                </div>
                <ul className="nav navbar-nav">
                <li className={this.state.productNav}><a href="/products">{this.props.page}{this.state.productNav} Product</a></li>
                <li className={this.setActiveNav(this.props.page)}><a href="/payment">{this.setActiveNav(this.props.page)}Payment</a></li>
                </ul>
                <ul className="nav navbar-nav navbar-right">
                <li><a href="#"><span className="glyphicon glyphicon-user"></span> Sign Up</a></li>
                <li><a href="#"><span className="glyphicon glyphicon-log-in"></span> Login</a></li>
                </ul>
            </div>
            </nav>

                       
            
        
        );
    }
}

export default Nav;
