import Nav from '../component/nav';
import '../component/css/product.css';
import axios from 'axios';
import { useEffect, useState } from 'react';
import { validateBody } from '../middleware/validateBody.middleware';
import {ProductSchema} from '../DTO/product.dto';

function Products() {

  var id_delete=-1;
  var id_update=-1;
  // const [List_product, setListProduct] = useState([]);

  const [product,setProduct]= useState([]);
  useEffect(()=> {
    axios.get('http://127.0.0.1:8000/products/')
      .then(res => {
        setProduct(res.data);
      })
      .catch(err => console.log(err));

  },[]);

  // window.onload = function() {
  //   loadRecord();
  // };

  function insertProduct(){
    try {
      const inputProduct = {
        name: document.getElementById('name').value,
        img: document.getElementById('img').value,
        price: document.getElementById('price').value,
      };
     
      const checkInput = validateBody(ProductSchema, inputProduct);
      if (checkInput.status === "fail") {
        alert(checkInput.message);
        return;
      }
      

      axios.post('http://127.0.0.1:8000/products/', inputProduct)
          .then(data => {
            document.getElementById("insertproduct").reset();
            const idproduct= List_product.length +1;
            List_product = [...List_product, 
              <tr key={idproduct}>
                  <td className="col-md-1">{idproduct}</td>
                  <td className="col-md-1"> <img src="fdsdsf" className="picture " style={{width: "30px"}} alt="Picture"></img></td>
                  <td>item.name</td>
                  <td className="col-md-2">item.price</td>
                  <td className="col-md-2"><button onClick={() => Update(1)} id="update" type="button" className="btn btn-primary" data-toggle="modal" data-target="#updateModal"> <i className="fas fa-edit"></i> </button>
                  <button onClick={() => Delete(1)} style={{marginLeft: "5px"}}  type="button" className="btn btn-danger" id="delete"> <i className="fas fa-trash-alt"></i> </button>
                  </td>
                </tr>
            ]
            console.log(List_product);
            alert("Insert successfullly");
          })
          .catch(err => alert("Insert failed"));
      
      
    } catch (error) {
      alert("Insert failed");
      throw error;
    }
  }

  function Delete(id){
    id_delete=id;
    console.log("delete ", id_delete);
  }

  
  function Update(id){
    id_update=id;
    console.log("update ",id_update);
  }

  var List_product = product.map((item,index) =>
                <tr key={index}>
                  <td className="col-md-1">{item.id}</td>
